import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import anime from 'animejs';
import styles from './css/LoginForm.module.css';

const LoginForm = () => {
    const [memberId, setMemberId] = useState('');
    const [memberPasswd, setMemberPasswd] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        let current = null;
        const emailInput = document.querySelector('#memberId');
        const passwordInput = document.querySelector('#memberPasswd');
        const submitButton = document.querySelector('#submit');

        const handleFocus = (offset, dasharray) => {
            if (current) current.pause();
            current = anime({
                targets: 'path',
                strokeDashoffset: {
                    value: offset,
                    duration: 700,
                    easing: 'easeOutQuart'
                },
                strokeDasharray: {
                    value: dasharray,
                    duration: 700,
                    easing: 'easeOutQuart'
                }
            });
        };

        emailInput.addEventListener('focus', () => handleFocus(0, '240 1386'));
        passwordInput.addEventListener('focus', () => handleFocus(-336, '240 1386'));
        submitButton.addEventListener('focus', () => handleFocus(-730, '530 1386'));

        // Adding a mouse down and up event to the document to manage the focus state explicitly
        document.addEventListener('mousedown', (e) => {
            if (e.target === submitButton) {
                handleFocus(-730, '530 1386');
            }
        });

        document.addEventListener('mouseup', (e) => {
            if (e.target === submitButton) {
                handleFocus(-730, '530 1386');
            }
        });

        return () => {
            emailInput.removeEventListener('focus', () => handleFocus(0, '240 1386'));
            passwordInput.removeEventListener('focus', () => handleFocus(-336, '240 1386'));
            submitButton.removeEventListener('focus', () => handleFocus(-730, '530 1386'));
            document.removeEventListener('mousedown', (e) => {
                if (e.target === submitButton) {
                    handleFocus(-730, '530 1386');
                }
            });
            document.removeEventListener('mouseup', (e) => {
                if (e.target === submitButton) {
                    handleFocus(-730, '530 1386');
                }
            });
        };
    }, []);

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:9999/api/auth/login', {
                memberId,
                memberPasswd
            });

            const { token } = response.data;
            localStorage.setItem('token', token);

            const decodedToken = jwtDecode(token);

            const currentTime = Date.now() / 1000;
            if (decodedToken.exp < currentTime) {
                setError('로그인 유효 시간이 만료됐습니다. 다시 로그인해주세요.');
                localStorage.removeItem('token');
                return;
            }

            const userRole = decodedToken.role;
            if (userRole === '관리자') {
                navigate('/admin');
            } else {
                navigate('/');
            }
        } catch (err) {
            setError('id 또는 비밀번호 오류');
        }
    };

    return (
        <div className={styles.loginFormPage}>
            <div className={styles.loginFormContainer}>
                <div className={styles.loginFormLeft}>
                    <div className={styles.loginFormLogin}>Login</div>
                    {error && <div className={styles.loginFormErrormessages}>{error}</div>}
                </div>
                <div className={styles.loginFormRight}>
                    <svg className={styles.svgBackground} viewBox="0 0 320 500">
                        <defs>
                            <linearGradient id="linearGradient" x1="13" y1="193.49992" x2="307" y2="193.49992" gradientUnits="userSpaceOnUse">
                                <stop offset="0" style={{ stopColor: '#000000' }} />
                                <stop offset="1" style={{ stopColor: '#ffffff' }} />
                            </linearGradient>
                        </defs>
                        <path className={styles.loginFormPath} d="m 40,120.00016 239.99984,-3.2e-4 c 0,0 24.99263,0.79932 25.00016,35.00016 0.008,34.20084 -25.00016,35 -25.00016,35 h -239.99984" fill="none" />
                        <path id="hoverPath" className={`${styles.loginFormPath} ${styles.loginFornHiddenPath}`} d="m 40, 220.00032 h 215 c 0,0 20,-0.99604 20,-25 0,-24.00396 -20,-25 -20,-25 h -190 c 0,0 -20,1.71033 -20,25 0,24.00396 20,25 20,25 h 168.57143" fill="none" />
                    </svg>
                    <div className={styles.loginFormInput}>
                        <form onSubmit={handleLogin}>
                            <label htmlFor="memberId">Email</label>
                            <input
                                type="text"
                                id="memberId"
                                value={memberId}
                                onChange={(e) => setMemberId(e.target.value)}
                                required
                            />
                            <label htmlFor="memberPasswd">Password</label>
                            <input
                                type="password"
                                id="memberPasswd"
                                value={memberPasswd}
                                onChange={(e) => setMemberPasswd(e.target.value)}
                                required
                            />
                            <div className={styles.idPasswdRecovery}>
                                <button type="button" className={styles.loginRecovery} lang="ko">아이디 찾기</button>
                                <span className={styles.loginSeparator}>|</span>
                                <button type="button" className={styles.loginRecovery} lang="ko">비밀번호 찾기</button>
                            </div>
                            <input
                                type="submit"
                                id="submit"
                                value="로그인"
                                lang="ko"
                                style={{
                                    color: '#707075',
                                    marginTop: '40px',
                                    transition: 'color 300ms'
                                }}
                                onBlur={(e) => {
                                    e.target.style.color = '#707075';
                                }}
                            />
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default LoginForm;
