import React, { useEffect, useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../../../services/AuthContext';
import axios from 'axios';
import ModalPopup from '../../modalPopup/ModalPopup';

const NaverAuthCallback = () => {
  const { handleOAuthCallback } = useContext(AuthContext);
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');

  useEffect(() => {
    const { hash } = window.location;
    const params = new URLSearchParams(hash.substring(1)); // '#'를 제거하고 파싱
    const accessToken = params.get('access_token');
    
    const processOAuthCallback = async (accessToken) => {
      try {
        await handleOAuthCallback(accessToken, 'naver');
        setLoading(false);
        navigate('/productPage');
      } catch (error) {
        console.error('OAuth callback:', error.message);
        setModalMessage('Failed to process OAuth callback');
        setShowModal(true);
      }
    };

    if (accessToken) {
      localStorage.setItem('authToken', accessToken);
      fetchUserProfile(accessToken);
    } else {
      //토큰이 없을 시
      setModalMessage('토큰 가져오기 실패');
      setShowModal(true);
    }
  }, [navigate]);

  const fetchUserProfile = (accessToken) => {
    const url = "https://openapi.naver.com/v1/nid/me";
    const headers = {
      'Authorization': `Bearer ${accessToken}`
    };

    axios.get(url, { headers })
      .then(response => {
        if (response.status === 200) {
          setProfile({
            name: response.data.response.name,
            email: response.data.response.email
          });
          setLoading(false);
          navigate('/productPage'); //로그인 후 이동할 페이지
        } else {
          setModalMessage('로그인 실패');
          setShowModal(true);
        }
      })
      .catch(error => {
        console.log('사용자 프로필 가져오기 실패:', error);
        setModalMessage('사용자 프로필 가져오기 실패');
        setShowModal(true);
      });
  };

  const handleModalClose = () => {
    setShowModal(false);
    window.close();
  };

  return (
    <div>
      {loading ? (
        <p>로그인 처리 중...</p>
      ) : (
        <div>
          <p>이름: {profile.name}</p>
          <p>이메일: {profile.email}</p>
        </div>
      )}
      <ModalPopup
        show={showModal}
        onClose={handleModalClose}
        onConfirm={handleModalClose}
        message={modalMessage}
        isConfirmation={false}
      />
    </div>
  );
};

export default NaverAuthCallback;
