import React, { useState } from 'react';
import AdminHeader from '../AdminHeader/AdminHeader';
import styles from './AdminReports.module.css';

const AdminReports = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchDateTerm, setSearchDateTerm] = useState({ startDate: '', endDate: '' });
  const [selectedSearchOption, setSelectedSearchOption] = useState('reportStatus');
  const [selectedReportStatus, setSelectedReportStatus] = useState('');
  const [searchResults, setSearchResults] = useState([

  ]);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSearch = () => {
    console.log('Searching for:', searchTerm);
    // Implement search logic here
  };

  const handleReset = () => {
    setSearchTerm({ text: '', startDate: '', endDate: '' });
    setSearchResults([]);
    console.log('조건 및 검색 결과 초기화');
  };

  const handleSearchOptionChange = (event) => {
    setSelectedSearchOption(event.target.value);
    setSearchTerm({ text: '', startDate: '', endDate: '' });
  };

  const handleStatusChange = (event) => {
    setSelectedReportStatus(event.target.value);
  }

  return (
    <div>
      <AdminHeader />
      <div className={styles.reportsContent}>
        <h2 className={styles.reportsTitle}>신고내역 관리</h2>
        <hr></hr>
        <div className={styles.searchFilterSection}>
          <div className={styles.searchCondition}>
            <label className={styles.labelMargin}>검색조건</label>
            <div className={styles.searchBox}>
              <select className={styles.selectInput} value={selectedSearchOption} onChange={handleSearchOptionChange}>
                <option value="reportStatus">신고 처리 상태</option>
                <option value="productNo">상품번호</option>
                <option value="sellerId">판매자 아이디</option>
                <option value="claimerId">신고자 아이디</option>
                <option value="reportDate">신고일자</option>
              </select>
              {selectedSearchOption === 'reportDate' ? (
                <div>
                  <input
                    className={styles.searchDateInput1}
                    type="date"
                    placeholder="시작일"
                    value={searchTerm.startDate}
                    onChange={(e) => setSearchTerm({ ...searchTerm, startDate: e.target.value })}
                  />
                  <input
                    className={styles.searchDateInput2}
                    type="date"
                    placeholder="종료일"
                    value={searchTerm.endDate}
                    onChange={(e) => setSearchTerm({ ...searchTerm, endDate: e.target.value })}
                  />
                </div>
              ) : (
                <input
                  className={styles.searchInput}
                  type="text"
                  placeholder="검색어 입력"
                  value={searchTerm}
                  onChange={handleSearchChange}
                />
              )}
            </div>
          </div>
          <div className={styles.buttonGroup}>
            <button className={styles.searchButton} onClick={handleSearch}>검색</button>
            <button className={styles.resetButton} onClick={handleReset}>초기화</button>
          </div>
        </div>
        <div className={styles.resultInfo}>
          <span>일치하는 신고: {searchResults.length}건</span>
        </div>
        <div className={styles.membersTable}>
          <table>
            <thead>
              <tr>
                <th>상품 번호</th>
                <th>신고 내용</th>
                <th>상품 판매글</th>
                <th>신고자</th>
                <th>신고일자</th>
                <th>처리 상태</th>
              </tr>
            </thead>
            <tbody>
              {searchResults.map((result, index) => (
                <tr key={index}>
                  <td>{result.productNo}</td>
                  <td>{result.reportContent}</td>
                  <td>{result.productTitle}</td>
                  <td>{result.claimerId}</td>
                  <td>{result.reportDate}</td>
                  <td>
                    <select className={styles.selectReportStatus} value={selectedReportStatus} onChange={handleStatusChange}>
                      <option value="N">신규</option>
                      <option value="P">처리 중</option>
                      <option value="C">완료</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AdminReports;
