import React, { useState, useEffect } from 'react';
import LoginForm from './LoginForm';
import RegisterForm from './RegisterForm';
import ModalPopupForm from './ModalPopupForm';
import styles from './css/LoginForm.module.css';
import $ from 'jquery';

const AuthModal = () => {
  const [isModalOpen, setIsModalOpen] = useState(true);
  const [isLogin, setIsLogin] = useState(true);

  useEffect(() => {
    $(".loginFormAlt2").click(function () {
      if (!$(this).hasClass('loginFormMaterialButton')) {
        $(".loginFormShape").css({
          "width": "100%",
          "height": "100%",
          "transform": "rotate(0deg)"
        });

        setTimeout(function () {
          $(".loginFormOverbox").css({
            "overflow": "initial"
          });
        }, 600);

        $(this).animate({
          "width": "140px",
          "height": "140px"
        }, 500, function () {
          $(".loginFormBox").removeClass("back");

          $(this).removeClass('active');
        });

        $(".loginFormOverbox .loginFormTitle").fadeOut(300);
        $(".loginFormOverbox .loginFormInput").fadeOut(300);
        $(".loginFormOverbox .loginFormButton").fadeOut(300);

        $(".loginFormAlt2").addClass('loginFormMaterialButton');
      }
    });

    
  }, []);

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  const toggleView = () => {
    setIsLogin(!isLogin);
  };

  return (
    <ModalPopupForm isOpen={isModalOpen} onClose={handleCloseModal}>
      <div className={styles.loginFormMaterialContainer}>
        <div className={styles.loginFormBox}>
          {isLogin ? <LoginForm /> : <RegisterForm />}
          <div className={`${styles.loginFormMaterialButton} ${styles.loginFormAlt2}`} onClick={toggleView}>
            <span className={styles.loginFormShape}></span>
          </div>
        </div>
      </div>
    </ModalPopupForm>
  );
};

export default AuthModal;
