import React from 'react';
import styles from './css/LoginButtons.module.css';

const KakaoLoginButton = ({ client_id, redirectUri }) => {
  const handleKakaoLogin = () => {
    console.log('clientId:', clientId); // Debug log
    console.log('redirectUri:', redirectUri); // Debug log

    const authUrl = `https://kauth.kakao.com/oauth/authorize?response_type=code&client_id=${client_id}&redirect_uri=${encodeURIComponent(redirectUri)}`;
    window.location.href = authUrl;
  };

  return (
    <img
      src="/img/kakao_btn_r.webp" // Path to Kakao login button image
      alt="카카오 로그인"
      onClick={handleKakaoLogin}
      className={styles.kakaoLoginIcon}
    />
  );
};

export default KakaoLoginButton;
