import React, { useEffect, useState, useContext } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import ModalPopup from '../../modalPopup/ModalPopup';

const KakaoAuthCallback = () => {
  const { setIsAuthenticated, setProfile } = useContext(AuthContext);
  const navigate = useNavigate();
  const location = useLocation();
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');

  useEffect(() => {
    const query = new URLSearchParams(location.search);
    const accessToken = query.get('access_token');

    const fetchUserProfile = async (accessToken) => {
      try {
        const response = await axios.get('https://kapi.kakao.com/v2/user/me', {
          headers: {
            Authorization: `Bearer ${accessToken}`,
          },
        });

        console.log('프로필 응답:', response.data);

        setProfile({
          nickname: response.data.properties.nickname,
        });
        setIsAuthenticated(true);
        setLoading(false);
        navigate('/productPage');
      } catch (error) {
        console.error('사용자 프로필 가져오기 실패:', error);
        setModalMessage('사용자 프로필 가져오기 실패');
        setShowModal(true);
      }
    };

    if (accessToken) {
      console.log('토큰 저장 및 handleOAuthCallback 호출');
      localStorage.setItem('token', accessToken);
      fetchUserProfile(accessToken);
    } else {
      setModalMessage('액세스 토큰 발급 실패');
      setShowModal(true);
    }
  }, [handleOAuthCallback, navigate, location]);

  const handleModalClose = () => {
    setShowModal(false);
  };

  return (
    <div>
      {loading ? (
        <p>로그인 처리 중...</p>
      ) : (
        <div>
          <p>닉네임: {profile.nickname}</p>
        </div>
      )}
      <ModalPopup
        show={showModal}
        onClose={handleModalClose}
        onConfirm={handleModalClose}
        message={modalMessage}
        isConfirmation={false}
      />
    </div>
  );
};

export default KakaoAuthCallback;
