import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import anime from 'animejs';
import styles from './css/LoginForm.module.css';

const LoginForm = () => {
    const [memberId, setMemberId] = useState('');
    const [memberPasswd, setMemberPasswd] = useState('');
    const [error, setError] = useState('');
    const [activeElement, setActiveElement] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const handleFocus = (element) => {
            setActiveElement(element);
        };

        const emailInput = document.querySelector('#memberId');
        const passwordInput = document.querySelector('#memberPasswd');
        const submitButton = document.querySelector('#submit');

        emailInput.addEventListener('focus', () => handleFocus('memberId'));
        passwordInput.addEventListener('focus', () => handleFocus('memberPasswd'));
        submitButton.addEventListener('focus', () => handleFocus('submit'));
        submitButton.addEventListener('mousedown', () => handleFocus('submit'));

        return () => {
            emailInput.removeEventListener('focus', () => handleFocus('memberId'));
            passwordInput.removeEventListener('focus', () => handleFocus('memberPasswd'));
            submitButton.removeEventListener('focus', () => handleFocus('submit'));
            submitButton.removeEventListener('mousedown', () => handleFocus('submit'));
        };
    }, []);

    useEffect(() => {
        if (activeElement) {
            let animationOffset = 0;
            if (activeElement === 'memberPasswd') {
                animationOffset = -336;
            } else if (activeElement === 'submit') {
                animationOffset = -730;
            }

            anime({
                targets: '.' + styles.loginFormPath,
                strokeDashoffset: {
                    value: animationOffset,
                    duration: 700,
                    easing: 'easeOutQuart'
                },
                strokeDasharray: {
                    value: activeElement === 'submit' ? '530 1386' : '240 1386',
                    duration: 700,
                    easing: 'easeOutQuart'
                }
            });
        }
    }, [activeElement]);

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:9999/api/auth/login', {
                memberId,
                memberPasswd
            });

            const { token } = response.data;
            localStorage.setItem('token', token);

            const decodedToken = jwtDecode(token);

            const currentTime = Date.now() / 1000;
            if (decodedToken.exp < currentTime) {
                setError('로그인 유효 시간이 만료됐습니다. 다시 로그인해주세요.');
                localStorage.removeItem('token');
                return;
            }

            const userRole = decodedToken.role;
            if (userRole === '관리자') {
                navigate('/admin');
            } else {
                navigate('/');
            }
        } catch (err) {
            setError('id 또는 비밀번호 오류');
        }
    };

    return (
        <div className={styles.loginFormPage}>
            <div className={styles.loginFormContainer}>
                <div className={styles.loginFormLeft}>
                    <div className={styles.loginFormLogin}>Login</div>
                    {error && <div className={styles.loginFormErrormessages}>{error}</div>}
                </div>
                <div className={styles.loginFormRight}>
                    <svg className={styles.svgBackground} viewBox="0 0 320 300">
                        <defs>
                            <linearGradient id="linearGradient" x1="13" y1="193.49992" x2="307" y2="193.49992" gradientUnits="userSpaceOnUse">
                                <stop offset="0" style={{ stopColor: '#000000' }} />
                                <stop offset="1" style={{ stopColor: '#ffffff' }} />
                            </linearGradient>
                        </defs>
                        <path className={styles.loginFormPath} d="m 40,120.00016 239.99984,-3.2e-4 c 0,0 24.99263,0.79932 25.00016,35.00016 0.008,34.20084 -25.00016,35 -25.00016,35 h -239.99984 c 0,-0.0205 -25,4.01348 -25,38.5 0,34.48652 25,38.5 25,38.5 h 215 c 0,0 20,-0.99604 20,-25 0,-24.00396 -20,-25 -20,-25 h -190 c 0,0 -20,1.71033 -20,25 0,24.00396 20,25 20,25 h 168.57143" fill="none" />
                    </svg>
                    <div className={styles.loginFormInput}>
                        <form onSubmit={handleLogin}>
                            <label htmlFor="memberId">Email</label>
                            <input
                                type="text"
                                id="memberId"
                                value={memberId}
                                onChange={(e) => setMemberId(e.target.value)}
                                required
                            />
                            <label htmlFor="memberPasswd">Password</label>
                            <input
                                type="password"
                                id="memberPasswd"
                                value={memberPasswd}
                                onChange={(e) => setMemberPasswd(e.target.value)}
                                required
                            />
                            <div className={styles.idPasswdRecovery}>
                                <button type="button" className={styles.loginRecovery} lang="ko">아이디 찾기</button>
                                <span className={styles.loginSeparator}>|</span>
                                <button type="button" className={styles.loginRecovery} lang="ko">비밀번호 찾기</button>
                            </div>
                            <input
                                type="submit"
                                id="submit"
                                value="로그인"
                                lang="ko"
                                onFocus={() => setActiveElement('submit')}
                                onMouseDown={() => setActiveElement('submit')}
                                style={{
                                    color: '#707075',
                                    marginTop: '40px',
                                    transition: 'color 300ms'
                                }}
                                onBlur={(e) => {
                                    e.target.style.color = '#707075';
                                }}
                            />
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default LoginForm;
