import React, { useState, useEffect } from 'react';
import LoginForm from './LoginForm';
import RegisterForm from './RegisterForm';
import ModalPopupForm from './ModalPopupForm';
import './AuthModal.css';

const AuthModal = () => {
  const [isLogin, setIsLogin] = useState(true);

  const toggleView = () => {
    setIsLogin(!isLogin);
  };

  return (
    <ModalPopupForm isOpen={true} onClose={() => { }}>
      <div className="loginFormMaterialContainer">
        <div className={`loginFormBox ${!isLogin && "back"}`}>
          {isLogin ? <LoginForm /> : <RegisterForm />}
          <div className={`loginFormMaterialButton loginFormAlt2 ${isLogin ? '' : 'active'}`} onClick={toggleView}>
            <span className="loginFormShape"></span>
          </div>
        </div>
      </div>
    </ModalPopupForm>
  );
};

export default AuthModal;
