import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import styles from './css/LoginForm.module.css';
import { jwtDecode } from 'jwt-decode';
import $ from 'jquery';

const LoginForm = () => {
    const [memberId, setMemberId] = useState('');
    const [memberPasswd, setMemberPasswd] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        $(".loginFormInput input").focus(function () {
            $(this).parent(".loginFormInput").each(function () {
                $("label", this).css({
                    "line-height": "18px",
                    "font-size": "18px",
                    "font-weight": "100",
                    "top": "0px"
                });
                $(".loginFormSpin", this).css({
                    "width": "100%"
                });
            });
        }).blur(function () {
            $(".loginFormSpin").css({
                "width": "0px"
            });
            if ($(this).val() === "") {
                $(this).parent(".loginFormInput").each(function () {
                    $("label", this).css({
                        "line-height": "60px",
                        "font-size": "24px",
                        "font-weight": "300",
                        "top": "10px"
                    });
                });
            }
        });

        $(".loginFormButton").click(function (e) {
            const pX = e.pageX;
            const pY = e.pageY;
            const oX = parseInt($(this).offset().left);
            const oY = parseInt($(this).offset().top);

            $(this).append('<span class="click-efect x-' + oX + ' y-' + oY + '" style="margin-left:' + (pX - oX) + 'px;margin-top:' + (pY - oY) + 'px;"></span>');
            $('.x-' + oX + '.y-' + oY + '').animate({
                "width": "500px",
                "height": "500px",
                "top": "-250px",
                "left": "-250px",
            }, 600);
            $("button", this).addClass('active');
        });
    }, []);

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:9999/api/auth/login', {
                memberId,
                memberPasswd
            });

            const { token } = response.data;
            localStorage.setItem('token', token);

            const decodedToken = jwtDecode(token);

            const currentTime = Date.now() / 1000;
            if (decodedToken.exp < currentTime) {
                setError('로그인 유효 시간이 만료됐습니다. 다시 로그인해주세요.');
                localStorage.removeItem('token');
                return;
            }

            const userRole = decodedToken.role;
            if (userRole === '관리자') {
                navigate('/admin');
            } else {
                navigate('/');
            }
        } catch (err) {
            setError('id 또는 비밀번호 오류');
        }
    };

    return (
        <div className={styles.loginFormBox}>
            <div className={styles.loginFormTitle}>로그인</div>
            <form onSubmit={handleLogin}>
                <div className={styles.loginFormInput}>
                    <input
                        type="text"
                        id="memberId"
                        value={memberId}
                        onChange={(e) => setMemberId(e.target.value)}
                        required
                        placeholder=" "
                    />
                    <label htmlFor="memberId">회원 아이디</label>
                    <span className={styles.loginFormSpin}></span>
                </div>
                <div className={styles.loginFormInput}>
                    <input
                        type="password"
                        id="memberPasswd"
                        value={memberPasswd}
                        onChange={(e) => setMemberPasswd(e.target.value)}
                        required
                        placeholder=" "
                    />
                    <label htmlFor="memberPasswd">Password</label>
                    <span className={styles.loginFormSpin}></span>
                </div>
                {error && <p style={{ color: 'red' }}>{error}</p>}
                <div className={`${styles.loginFormButton} ${styles.loginFormLogin}`}>
                    <button type="submit"><span>GO</span> <i className="fa fa-check"></i></button>
                </div>
            </form>
            <div className={styles.loginCredRecovery}>
                <a href="#" className={styles.loginFormPassForgot}>아이디 찾기</a><span>-</span><a href="#" className={styles.loginFormPassForgot}>비밀번호 찾기</a>
            </div>
        </div>
    );
};

export default LoginForm;
