import React, { useState, useEffect } from 'react';
import LoginForm from './LoginForm';
import RegisterForm from './RegisterForm';
import ModalPopupForm from './ModalPopupForm';
import styles from './css/LoginForm.module.css';
import $ from 'jquery';

const AuthModal = () => {
  const [isModalOpen, setIsModalOpen] = useState(true);
  const [isLogin, setIsLogin] = useState(true);

  useEffect(() => {
    $(".loginFormButton").click(function(e) {
      const pX = e.pageX,
            pY = e.pageY,
            oX = parseInt($(this).offset().left),
            oY = parseInt($(this).offset().top);

      $(this).append('<span class="loginFormClickEffect x-' + oX + ' y-' + oY + '" style="margin-left:' + (pX - oX) + 'px;margin-top:' + (pY - oY) + 'px;"></span>');
      $('.x-' + oX + '.y-' + oY + '').animate({
        "width": "500px",
        "height": "500px",
        "top": "-250px",
        "left": "-250px",
      }, 600);
      $("button", this).addClass('active');
    });
    $(".loginFormAlt2").click(function () {
      if (!$(this).hasClass('loginFormMaterialButton')) {
        $(".loginFormShape").css({
          "width": "100%",
          "height": "100%",
          "transform": "rotate(0deg)"
        });

        setTimeout(function () {
          $(".loginFormOverbox").css({
            "overflow": "initial"
          });
        }, 600);

        $(this).animate({
          "width": "140px",
          "height": "140px"
        }, 500, function () {
          $(".loginFormBox").removeClass("back");

          $(this).removeClass('active');
        });

        $(".loginFormOverbox .loginFormTitle").fadeOut(300);
        $(".loginFormOverbox .loginFormInput").fadeOut(300);
        $(".loginFormOverbox .loginFormButton").fadeOut(300);

        $(".loginFormAlt2").addClass('loginFormMaterialButton');
      }
    });

    $(".loginFormMaterialButton").click(function () {
      if ($(this).hasClass('loginFormMaterialButton')) {
        setTimeout(function () {
          $(".loginFormOverbox").css({
            "overflow": "hidden"
          });
          $(".loginFormBox").addClass("back");
        }, 200);
        $(this).addClass('active').animate({
          "width": "700px",
          "height": "700px"
        });

        setTimeout(function () {
          $(".loginFormShape").css({
            "width": "50%",
            "height": "50%",
            "transform": "rotate(45deg)"
          });

          $(".loginFormOverbox .loginFormTitle").fadeIn(300);
          $(".loginFormOverbox .loginFormInput").fadeIn(300);
          $(".loginFormOverbox .loginFormButton").fadeIn(300);
        }, 700);

        $(this).removeClass('loginFormMaterialButton');
      }

      if ($(".loginFormAlt2").hasClass('loginFormMaterialButton')) {
        $(".loginFormAlt2").removeClass('loginFormMaterialButton');
        $(".loginFormAlt2").addClass('loginFormMaterialButton');
      }
    });
  }, []);

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  const toggleView = () => {
    setIsLogin(!isLogin);
  };

  return (
    <ModalPopupForm isOpen={isModalOpen} onClose={handleCloseModal}>
      <div className={styles.loginFormMaterialContainer}>
        <div className={styles.loginFormBox}>
          {isLogin ? <LoginForm /> : <RegisterForm />}
          <div className={`${styles.loginFormMaterialButton} ${styles.loginFormAlt2}`} onClick={toggleView}>
            <span className={styles.loginFormShape}></span>
          </div>
        </div>
      </div>
    </ModalPopupForm>
  );
};

export default AuthModal;
