import React, { useEffect, useState, useContext } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import ModalPopup from '../../modalPopup/ModalPopup';
import { AuthContext } from '../../../services/AuthContext';

const KakaoAuthCallback = () => {
  const { handleOAuthCallback } = useContext(AuthContext);
  const navigate = useNavigate();
  const location = useLocation();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');

  useEffect(() => {
    const query = new URLSearchParams(location.search);
    const code = query.get('code');

    const fetchAccessToken = async (code) => {
      try {
        const params = new URLSearchParams({
          grant_type: 'authorization_code',
          client_id: 'your_kakao_client_id', // Replace with your actual client ID
          redirect_uri: 'http://localhost:3000/kakaoAuthCallback', // Replace with your actual redirect URI
          code,
        });

        const response = await axios.post('https://kauth.kakao.com/oauth/token', params, {
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded;charset=utf-8',
          },
        });

        const accessToken = response.data.access_token;
        localStorage.setItem('token', accessToken); // Store the token in local storage
        await handleOAuthCallback(accessToken); // Update the authentication state
        fetchUserProfile(accessToken);
      } catch (error) {
        console.error('Failed to fetch access token:', error.response ? error.response.data : error.message);
        setModalMessage('Failed to fetch access token');
        setShowModal(true);
      }
    };

    const fetchUserProfile = async (accessToken) => {
      try {
        const response = await axios.get('https://kapi.kakao.com/v2/user/me', {
          headers: {
            Authorization: `Bearer ${accessToken}`,
          },
        });

        console.log('Profile response:', response.data); // Debug log

        setProfile({
          nickname: response.data.properties.nickname,
          email: response.data.kakao_account.email,
        });
        setLoading(false);
        navigate('/productPage');
      } catch (error) {
        console.error('Failed to fetch user profile:', error);
        setModalMessage('Failed to fetch user profile');
        setShowModal(true);
      }
    };

    if (code) {
      fetchAccessToken(code);
    } else {
      setModalMessage('Failed to get authorization code');
      setShowModal(true);
    }
  }, [handleOAuthCallback, navigate, location]);

  const handleModalClose = () => {
    setShowModal(false);
  };

  return (
    <div>
      {loading ? (
        <p>로그인 처리 중...</p>
      ) : (
        <div>
          <p>닉네임: {profile.nickname}</p>
        </div>
      )}
      <ModalPopup
        show={showModal}
        onClose={handleModalClose}
        onConfirm={handleModalClose}
        message={modalMessage}
        isConfirmation={false}
      />
    </div>
  );
};

export default KakaoAuthCallback;
