import React, { useEffect } from 'react';
import styles from './css/LoginButtons.module.css';

const NaverLoginButton = ({ clientId, callbackUrl, serviceUrl }) => {
  useEffect(() => {
    const script = document.createElement('script');
    script.src = "https://static.nid.naver.com/js/naverLogin_implicit-1.0.3.js";
    script.type = "text/javascript";
    script.onload = () => initializeNaverLogin();
    document.head.appendChild(script);

    function initializeNaverLogin() {
      const naver_id_login = new window.naver_id_login(clientId, callbackUrl);
      const state = naver_id_login.getUniqState();
      naver_id_login.setButton("white", 1, 40);
      naver_id_login.setDomain(serviceUrl);
      naver_id_login.setState(state);
      naver_id_login.setPopup();

      naver_id_login.popup_window = function() {
        const width = 600;
        const height = 550;
        const left = (window.screen.width / 2) - (width / 2);
        const top = (window.screen.height / 2) - (height / 2);
        const options = `width=${width},height=${height},top=${top},left=${left},resizable=1,scrollbars=yes`;

        window.open(this.loginUrl, 'naverloginpop', options);
      };
      
      naver_id_login.init_naver_id_login();
    }

    return () => {
      document.head.removeChild(script);
    };
  }, [clientId, callbackUrl, serviceUrl]);

  return <div id="naver_id_login" className={styles.naver_id_login}></div>;
};

export default NaverLoginButton;
