import React, { useState } from 'react';
import AdminHeader from '../AdminHeader/AdminHeader';
import styles from './AdminMembers.module.css';

const AdminMembers = () => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSearch = () => {
    console.log('Searching for:', searchTerm);
    // Implement search logic here
  };

  const handleReset = () => {
    setSearchTerm('');
    setSearchResults([]);
    console.log('Filters and search reset');
  };

  return (
    <div>
      <AdminHeader />
      <div className={styles.membersContent}>
        <h2 className={styles.membersTitle}>회원 정보관리</h2>
        <hr></hr>
        <div className={styles.searchFilterSection}>
          <div className={styles.searchCondition}>
            <label className={styles.labelMargin}>검색조건</label>
            <div className={styles.searchBox}>
              <select className={styles.selectInput}>
                <option value="userId">아이디</option>
                <option value="name">이름</option>
                <option value="nickname">닉네임</option>
                <option value="email">이메일주소</option>
                <option value="phone">전화번호</option>
              </select>
              <input
                className={styles.searchInput}
                type="text"
                placeholder="검색어 입력"
                value={searchTerm}
                onChange={handleSearchChange}
              />
            </div>
          </div>
          <div className={styles.buttonGroup}>
            <button className={styles.searchButton} onClick={handleSearch}>검색</button>
            <button className={styles.resetButton} onClick={handleReset}>초기화</button>
          </div>
        </div>
        <div className={styles.resultInfo}>
          <span>총 회원수 : {searchResults.length}명</span>
        </div>
        <div className={styles.membersTable}>
          <table>
            <thead>
              <tr>
                <th>아이디</th>
                <th>회원이름</th>
                <th>닉네임</th>
                <th>이메일</th>
                <th>회원등급</th>
                <th>전화번호</th>
                <th>가입일자</th>
                <th>관리</th>
              </tr>
            </thead>
            <tbody>

            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AdminMembers;
