import React, { useEffect } from 'react';
import styles from './css/LoginButtons.module.css';

const KakaoLoginButton = ({ clientId, redirectUri }) => {
  const handleKakaoLogin = () => {
    const authUrl = `https://kauth.kakao.com/oauth/authorize?client_id=${clientId}&redirect_uri=${encodeURIComponent(redirectUri)}&response_type=code`;
    window.location.href = authUrl;
  };

  return (
    <img
      src="path/to/kakao-login-icon.png"
      alt="카카오 로그인"
      onClick={handleKakaoLogin}
      className={styles.kakaoLoginIcon}
    />
  );
};

export default KakaoLoginButton;
