import React from 'react';
import styles from './css/ConfirmPopup.module.css';

const ConfirmPopup = ({ show, onClose, onConfirm, message }) => {
  if (!show) return null;

  return (
    <div className={styles.modal} id="modal-one" aria-hidden="true">
      <div className={styles.modalDialog}>
        <div className={styles.modalHeader}>
          <h2>Confirm Action</h2>
          <button onClick={onClose} className={styles.btnClose} aria-hidden="true">×</button>
        </div>
        <div className={styles.modalBody}>
          <p>{message}</p>
        </div>
        <div className={styles.modalFooter}>
          <button onClick={onConfirm} className={styles.btn}>확인</button>
          <button onClick={onClose} className={styles.btn}>취소</button>
        </div>
      </div>
    </div>
  );
};

export default ConfirmPopup;
