import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import styles from './LoginForm.module.css';
import jwtDecode from 'jwt-decode';

const LoginForm = () => {
    const [memberId, setMemberId] = useState('');
    const [memberPasswd, setMemberPasswd] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const inputs = document.querySelectorAll(`.${styles.input} input`);

        inputs.forEach(input => {
            input.addEventListener('focus', handleFocus);
            input.addEventListener('blur', handleBlur);
        });

        return () => {
            inputs.forEach(input => {
                input.removeEventListener('focus', handleFocus);
                input.removeEventListener('blur', handleBlur);
            });
        };
    }, []);

    const handleFocus = (e) => {
        const parent = e.target.parentNode;
        parent.querySelector('label').style.cssText = "line-height: 18px; font-size: 18px; font-weight: 100; top: 0px;";
        parent.querySelector(`.${styles.spin}`).style.width = '100%';
    };

    const handleBlur = (e) => {
        const parent = e.target.parentNode;
        parent.querySelector(`.${styles.spin}`).style.width = '0px';
        if (e.target.value === '') {
            parent.querySelector('label').style.cssText = "line-height: 60px; font-size: 24px; font-weight: 300; top: 10px;";
        }
    };

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:9999/api/auth/login', {
                memberId,
                memberPasswd
            });

            const { token } = response.data;
            localStorage.setItem('token', token);

            const decodedToken = jwtDecode(token);

            const currentTime = Date.now() / 1000;
            if (decodedToken.exp < currentTime) {
                setError('로그인 유효 시간이 만료됐습니다. 다시 로그인해주세요.');
                localStorage.removeItem('token');
                return;
            }

            const userRole = decodedToken.role;
            if (userRole === '관리자') {
                navigate('/admin');
            } else {
                navigate('/');
            }
        } catch (err) {
            setError('id 또는 비밀번호 오류');
        }
    };

    return (
        <div className={styles.materialContainer}>
            <div className={styles.loginFormMaterialContainer}>
                <div className={`${styles.box} ${styles.loginFormBox}`}>
                    <div className={styles.title}>LOGIN</div>
                    <form onSubmit={handleLogin}>
                        <div className={styles.input}>
                            <label htmlFor="memberId">회원 아이디</label>
                            <input
                                type="text"
                                id="memberId"
                                value={memberId}
                                onChange={(e) => setMemberId(e.target.value)}
                                required
                            />
                            <span className={styles.spin}></span>
                        </div>
                        <div className={styles.input}>
                            <label htmlFor="memberPasswd">Password</label>
                            <input
                                type="password"
                                id="memberPasswd"
                                value={memberPasswd}
                                onChange={(e) => setMemberPasswd(e.target.value)}
                                required
                            />
                            <span className={styles.spin}></span>
                        </div>
                        {error && <p style={{ color: 'red' }}>{error}</p>}
                        <div className={styles.button}>
                            <button type="submit"><span>GO</span> <i className="fa fa-check"></i></button>
                        </div>
                    </form>
                    <a href="#" className={styles.passForgot}>Forgot your password?</a>
                </div>

                <div className={`${styles.box} ${styles.overbox}`}>
                    <div className={`${styles.materialButton} ${styles.alt2}`}><span className={styles.shape}></span></div>
                    <div className={styles.title}>REGISTER</div>
                    <div className={styles.input}>
                        <label htmlFor="regname">Username</label>
                        <input type="text" name="regname" id="regname" />
                        <span className={styles.spin}></span>
                    </div>
                    <div className={styles.input}>
                        <label htmlFor="regpass">Password</label>
                        <input type="password" name="regpass" id="regpass" />
                        <span className={styles.spin}></span>
                    </div>
                    <div className={styles.input}>
                        <label htmlFor="reregpass">Repeat Password</label>
                        <input type="password" name="reregpass" id="reregpass" />
                        <span className={styles.spin}></span>
                    </div>
                    <div className={styles.button}>
                        <button><span>NEXT</span></button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default LoginForm;
