import React, { useState, useEffect } from 'react';
import AdminHeader from '../AdminHeader/AdminHeader';
import axios from 'axios';
import styles from './AdminReports.module.css';

const AdminReports = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchDateTerm, setSearchDateTerm] = useState({ startDate: '', endDate: '' });
  const [selectedSearchOption, setSelectedSearchOption] = useState('productNo');
  const [searchResults, setSearchResults] = useState([]);
  const [unreadReports, setUnreadReports] = useState([]);
  const [expandedRows, setExpandedRows] = useState({});
  const [activeReadSection, setActiveReadSection] = useState(false); // Track if 'read' section is active

  useEffect(() => {
    fetchReports();
  }, []);

  const fetchReports = async (filters = {}) => {
    try {
      const response = await axios.get('http://localhost:9999/reports', {
        headers: {
          'Admin-Id': 'member1',
        },
        params: filters,
      });
      const allReports = response.data;

      const unread = allReports.filter(report => report.reportReadStatus === 'N');
      const read = allReports.filter(report => report.reportReadStatus !== 'N');
      setUnreadReports(unread);
      setSearchResults(read);
    } catch (error) {
      console.error('신고 내역 가져오기 오류:', error);
    }
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleDateSearchChange = (field, value) => {
    setSearchDateTerm({ ...searchDateTerm, [field]: value });
  };

  const handleSearch = () => {
    const filters = {};
    if (selectedSearchOption === 'reportDate') {
      filters.startDate = searchDateTerm.startDate;
      filters.endDate = searchDateTerm.endDate;
    } else {
      filters[selectedSearchOption] = searchTerm;
    }
    fetchFilteredReports(filters);
  };

  const fetchFilteredReports = async (filters) => {
    try {
      const response = await axios.get('http://localhost:9999/reports/filtered', {
        headers: {
          'Admin-Id': 'member1',
        },
        params: filters,
      });
      const allReports = response.data;

      const unread = allReports.filter(report => report.reportReadStatus === 'N');
      const read = allReports.filter(report => report.reportReadStatus !== 'N');
      setUnreadReports(unread);
      setSearchResults(read);
    } catch (error) {
      console.error('신고 내역 가져오기 오류:', error);
    }
  };

  const handleReset = () => {
    setSearchTerm('');
    setSearchDateTerm({ startDate: '', endDate: '' });
    fetchReports();
  };

  const handleSearchOptionChange = (event) => {
    setSelectedSearchOption(event.target.value);
    setSearchTerm('');
    setSearchDateTerm({ startDate: '', endDate: '' });
  };

  const handleStatusChange = async (event, report) => {
    const updatedStatus = event.target.value;
    try {
      const response = await axios.put('http://localhost:9999/reports', {
        productNo: report.productNo,
        sellerId: report.sellerId,
        reportStatus: updatedStatus,
      }, {
        headers: {
          'Admin-Id': 'member1',
          'Claimer-Id': report.claimerId,
        },
      });
      if (response.status === 200) {
        report.reportStatus = updatedStatus;
        setSearchResults([...searchResults]);
        setUnreadReports([...unreadReports]);
      } else {
        console.error('신고 상태 수정 오류:', response.data);
      }
    } catch (error) {
      console.error('신고 상태 수정 오류:', error);
    }
  };

  const handleSectionMouseEnter = (section) => {
    if (section === 'read') {
      setActiveReadSection(true);
    }
  };

  const handleSectionMouseLeave = (section) => {
    if (section === 'read') {
      setActiveReadSection(false);
    }
  };

  const handleToggleRow = (index) => {
    setExpandedRows(prevState => ({
      ...prevState,
      [index]: !prevState[index]
    }));
  };

  return (
    <div>
      <AdminHeader />
      <div className={styles.reportsContent}>
        <h2 className={styles.reportsTitle}>신고내역 관리</h2>
        <hr />
        <div className={styles.searchFilterSection}>
          <div className={styles.searchCondition}>
            <label className={styles.labelMargin}>검색조건</label>
            <div className={styles.searchBox}>
              <select className={styles.selectInput} value={selectedSearchOption} onChange={handleSearchOptionChange}>
                <option value="productNo">상품번호</option>
                <option value="sellerId">판매자 아이디</option>
                <option value="claimerId">신고자 아이디</option>
                <option value="reportDate">신고일자</option>
              </select>
              {selectedSearchOption === 'reportDate' ? (
                <div>
                  <input
                    className={styles.searchDateInput1}
                    type="date"
                    placeholder="시작일"
                    value={searchDateTerm.startDate}
                    onChange={(e) => handleDateSearchChange('startDate', e.target.value)}
                  />
                  <input
                    className={styles.searchDateInput2}
                    type="date"
                    placeholder="종료일"
                    value={searchDateTerm.endDate}
                    onChange={(e) => handleDateSearchChange('endDate', e.target.value)}
                  />
                </div>
              ) : (
                <input
                  className={styles.searchInput}
                  type="text"
                  placeholder="검색어 입력"
                  value={searchTerm}
                  onChange={handleSearchChange}
                />
              )}
            </div>
          </div>
          <div className={styles.buttonGroup}>
            <button className={styles.searchButton} onClick={handleSearch}>검색</button>
            <button className={styles.resetButton} onClick={handleReset}>초기화</button>
          </div>
        </div>
        <div
          className={`${styles.section} ${unreadReports.length > 0 ? styles.activeSection : ''}`}
        >
          <div className={styles.resultInfo}>
            <span>읽지 않은 신고: {unreadReports.length}건</span>
            {unreadReports.length > 0 && <span className={styles.notificationDot}></span>}
          </div>
          <div className={styles.membersTable}>
            <table>
              <thead>
                <tr>
                  <th>상품 번호</th>
                  <th>신고 내용</th>
                  <th>상품 판매글</th>
                  <th>신고자</th>
                  <th>신고일자</th>
                  <th>처리 상태</th>
                </tr>
              </thead>
              <tbody>
                {unreadReports.map((result, index) => (
                  <React.Fragment key={index}>
                    <tr>
                      <td>{result.productNo}</td>
                      <td className={styles.truncate} onClick={() => handleToggleRow(index)}>{result.reportContent}</td>
                      <td>{result.productTitle}</td>
                      <td>{result.claimerId}</td>
                      <td>{result.reportDate}</td>
                      <td>
                        <select
                          className={styles.selectReportStatus}
                          value={result.reportStatus}
                          onChange={(event) => handleStatusChange(event, result)}
                        >
                          <option value="N">신규</option>
                          <option value="P">처리 중</option>
                          <option value="C">완료</option>
                        </select>
                      </td>
                    </tr>
                    {
                      expandedRows[index] && (
                        <tr className={styles.expandableRow}>
                          <td colSpan="6" className={styles.expandableContent}>{result.reportContent}</td>
                        </tr>
                      )
                    }
                  </React.Fragment>
                ))}
              </tbody>
            </table>
          </div>
        </div>
        <div
          className={`${styles.section} ${activeReadSection ? styles.activeSection : ''}`}
          onMouseEnter={() => handleSectionMouseEnter('read')}
          onMouseLeave={() => handleSectionMouseLeave('read')}
        >
          <div className={styles.resultInfo}>
            <span>읽은 신고: {searchResults.length}건</span>
          </div>
          <div className={styles.membersTable}>
            <table>
              <thead>
                <tr>
                  <th>상품 번호</th>
                  <th>신고 내용</th>
                  <th>상품 판매글</th>
                  <th>신고자</th>
                  <th>신고일자</th>
                  <th>처리 상태</th>
                </tr>
              </thead>
              <tbody>
                {searchResults.map((result, index) => (
                  <React.Fragment key={index}>
                    <tr>
                      <td>{result.productNo}</td>
                      <td className={styles.truncate} onClick={() => handleToggleRow(index + unreadReports.length)}>{result.reportContent}</td>
                      <td>{result.productTitle}</td>
                      <td>{result.claimerId}</td>
                      <td>{result.reportDate}</td>
                      <td>
                        <select
                          className={styles.selectReportStatus}
                          value={result.reportStatus}
                          onChange={(event) => handleStatusChange(event, result)}
                        >
                          <option value="N">신규</option>
                          <option value="P">처리 중</option>
                          <option value="C">완료</option>
                        </select>
                      </td>
                    </tr>
                    {expandedRows[index + unreadReports.length] && (
                      <tr className={styles.expandableRow}>
                        <td colSpan="6" className={styles.expandableContent}>{result.reportContent}</td>
                      </tr>
                    )}
                  </React.Fragment>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminReports;
