import React, { useState, useEffect } from 'react';
import MemberService from '../../../services/MemberService';
import AdminHeader from '../AdminHeader/AdminHeader';
import styles from './AdminMembers.module.css';

const AdminMembers = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchField, setSearchField] = useState('memberId');
  const [searchResults, setSearchResults] = useState([]);
  const [members, setMembers] = useState([]);

  useEffect(() => {
    fetchMembers();
  }, []);

  const fetchMembers = async () => {
    try {
      const response = await MemberService.getAllMembers();
      setMembers(response.data);
      setSearchResults(response.data);
    } catch (error) {
      console.error('회원 정보 가져오기 오류:', error);
    }
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSearchFieldChange = (event) => {
    setSearchField(event.target.value);
  };

  const handleSearch = async () => {
    console.log('검색 버튼 클릭');
    try {
      const response = await MemberService.searchMembers({
        [searchField]: searchTerm,
      });
      console.log('검색 결과:', response);
      setSearchResults(response.data);
    } catch (error) {
      console.error('회원 검색 오류:', error);
    }
  };

  const handleReset = () => {
    setSearchTerm('');
    setSearchResults(members);
    console.log('조건 및 검색 결과 초기화');
  };

  const handleUpdate = async (member) => {
    try {
      const response = await MemberService.updateMember(member);
      alert(response.data.msg);
      fetchMembers();
    } catch (error) {
      console.error('Error updating member:', error);
    }
  };

  const handleDelete = async (memberId) => {
    try {
      const response = await MemberService.deleteMember(memberId);
      alert(response.data.msg);
      fetchMembers();
    } catch (error) {
      console.error('Error deleting member:', error);
    }
  };

  return (
    <div>
      <AdminHeader />
      <div className={styles.membersContent}>
        <h2 className={styles.membersTitle}>회원 정보관리</h2>
        <hr></hr>
        <div className={styles.searchFilterSection}>
          <div className={styles.searchCondition}>
            <label className={styles.labelMargin}>검색조건</label>
            <div className={styles.searchBox}>
              <select className={styles.selectInput} value={searchField} onChange={handleSearchFieldChange}>
                <option value="memberId">아이디</option>
                <option value="memberName">이름</option>
                <option value="memberNick">닉네임</option>
                <option value="memberEmail">이메일주소</option>
                <option value="memberPhoneNo">전화번호</option>
              </select>
              <input
                className={styles.searchInput}
                type="text"
                placeholder="검색어 입력"
                value={searchTerm}
                onChange={handleSearchChange}
              />
            </div>
          </div>
          <div className={styles.buttonGroup}>
            <button className={styles.searchButton} onClick={handleSearch}>검색</button>
            <button className={styles.resetButton} onClick={handleReset}>초기화</button>
          </div>
        </div>
        <div className={styles.resultInfo}>
          <span>총 회원 수 : {searchResults.length}명</span>
        </div>
        <div className={styles.membersTable}>
          <table>
            <thead>
              <tr>
                <th>아이디</th>
                <th>회원이름</th>
                <th>닉네임</th>
                <th>이메일</th>
                <th>회원등급</th>
                <th>전화번호</th>
                <th>가입일자</th>
                <th>관리</th>
              </tr>
            </thead>
            <tbody>
              {searchResults.map((result, index) => (
                result && (
                  <tr key={index}>
                    <td>{result.memberId}</td>
                    <td>{result.memberName}</td>
                    <td>{result.memberNick}</td>
                    <td>{result.memberEmail}</td>
                    <td>{result.memberGradeName}</td>
                    <td>{result.memberPhoneNo}</td>
                    <td>{result.memberRegDate}</td>
                    <td>
                      <button className={styles.editButton} onClick={() => handleUpdate(result)}>수정</button>
                      <button className={styles.deleteButton} onClick={() => handleDelete(result.memberId)}>삭제</button>
                    </td>
                  </tr>
                )
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AdminMembers;
