import React, { createContext, useState, useEffect } from 'react';
import { jwtDecode } from 'jwt-decode';
import axios from 'axios';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);
  const [profile, setProfile] = useState(null);

  const checkTokenValidity = async () => {
    const token = localStorage.getItem('token');
    const tokenProvider = localStorage.getItem('tokenProvider');

    if (token) {
      try {
        const decodedToken = jwtDecode(token);
        const currentTime = Date.now() / 1000;
        if (decodedToken.exp < currentTime) {
          localStorage.removeItem('token');
          setIsAuthenticated(false);
        } else {
          setIsAuthenticated(true);
          setProfile(decodedToken);
        }
      } catch (error) {
        console.error('Failed to decode token:', error.message);
        // JWT 토큰이 아닌 경우
        if (tokenProvider === 'naver') {
          await validateANaverakaoToken(token);
        } else if (tokenProvider === 'naver') {
          await validateNaverToken(token);
        } else {
          localStorage.removeItem('token');
          localStorage.removeItem('tokenProvider');
          setIsAuthenticated(false);
        }
      }
    }
    setLoading(false);
  };

  useEffect(() => {
    checkTokenValidity();
  }, []);

  const handleOAuthCallback = async (accessToken) => {
    localStorage.setItem('token', accessToken);
    try {
      const response = await axios.get('https://kapi.kakao.com/v2/user/me', {
        headers: {
          Authorization: `Bearer ${accessToken}`,
        },
      });
      setProfile({
        nickname: response.data.properties.nickname,
        email: response.data.kakao_account.email,
      });
      setIsAuthenticated(true);
    } catch (error) {
      console.error('Failed to fetch user profile:', error.message);
    }
  };

  return (
    <AuthContext.Provider value={{ isAuthenticated, setIsAuthenticated, profile, setProfile, handleOAuthCallback }}>
      {!loading ? children : <div>Loading...</div>}
    </AuthContext.Provider>
  );
};
